INSERT INTO `` (`id`,`page`,`sura_name`,`aya_no`,`quranic_words`,`explanation`) VALUES (11334,602,'قريش',1,'لِإِيلَٰفِ قُرَيۡشٍ','اعْجَبُوا لِعادَةِ قُرَيشٍ. وقُرِيشٌ: اسمُ قَبِيلةٍ.');
INSERT INTO `` (`id`,`page`,`sura_name`,`aya_no`,`quranic_words`,`explanation`) VALUES (11335,602,'قريش',2,'إِۦلَٰفِهِمۡ رِحۡلَةَ ٱلشِّتَآءِ وَٱلصَّيۡفِ','تَعَوُّدِهِم عَلى انْتِظامِ رِحْلَتَيهِم في الشِّتاءِ إلى «اليمن»، وفي الصَّيفِ إلى «الشَّامِ». والرِّحْلةُ: اسْمٌ للارْتِحالِ.');
