INSERT INTO `` (`id`,`page`,`sura_name`,`aya_no`,`quranic_words`,`explanation`) VALUES (11342,602,'الماعون',6,'ٱلَّذِينَ هُمۡ يُرَآءُونَ','الَّذِين هُم يَعْمَلُونَ الخيرَ مُراءَاةً للنَّاسِ.');
