INSERT INTO `` (`id`,`page`,`sura_name`,`aya_no`,`quranic_words`,`explanation`) VALUES (11344,602,'الكوثر',1,'ٱلۡكَوۡثَرَ','الخيرَ الكَثِيرَ في الدُّنيا والآخِرَةِ، وَمِن ذَلِكَ نَهرُ الكَوْثَرِ في الجنَّةِ.');
