INSERT INTO `` (`id`,`page`,`sura_name`,`aya_no`,`quranic_words`,`explanation`) VALUES (11348,603,'الكافرون',2,'لَآ أَعۡبُدُ مَا تَعۡبُدُونَ','لا تَحصُلُ منِّي عِبادَةُ ما تَعْبُدُونَ مِنَ الأصْنامِ والآلِهَةَ الزَّائِفَةِ.');
INSERT INTO `` (`id`,`page`,`sura_name`,`aya_no`,`quranic_words`,`explanation`) VALUES (11349,603,'الكافرون',3,'وَلَآ أَنتُمۡ عَٰبِدُونَ مَآ أَعۡبُدُ','وما أنتُم عابِدُونَ في المسْتَقْبَلِ ما أَعْبُدُ مِنْ إلهٍٍ واحدٍ، هُوَ اللهُ رَبُّ العالمَينَ المُسْتَحِقُّ وَحْدَهُ لِلعِبادةِ.');
INSERT INTO `` (`id`,`page`,`sura_name`,`aya_no`,`quranic_words`,`explanation`) VALUES (11350,603,'الكافرون',4,'وَلَآ أَنَا۠ عَابِدٞ مَّا عَبَدتُّمۡ','وَلا أَنا عابِدٌ ما عبدتُم مِنَ الأصْنامِ والآلِهَةِ الباطِلةِ فيما مَضَى مِنَ الأزْمانِ.');
INSERT INTO `` (`id`,`page`,`sura_name`,`aya_no`,`quranic_words`,`explanation`) VALUES (11351,603,'الكافرون',5,'وَلَآ أَنتُمۡ عَٰبِدُونَ مَآ أَعۡبُدُ','وَلَا أنتُم عابِدُونَ مُسْتَقْبلاً ما أَعْبُدُ.');
INSERT INTO `` (`id`,`page`,`sura_name`,`aya_no`,`quranic_words`,`explanation`) VALUES (11352,603,'الكافرون',6,'لَكُمۡ دِينُكُمۡ','الَّذِي أَصْرَرْتُم على اتِّباعِهِ، يختصُّ بِكُم وأنا بَرِيءٌ مِنْهُ.');
INSERT INTO `` (`id`,`page`,`sura_name`,`aya_no`,`quranic_words`,`explanation`) VALUES (11353,603,'الكافرون',6,'وَلِيَ دِينِ','الَّذِي أنا مُخْتَصٌّ بِهِ لا تَشْرَكُونِي فِيه، وَلَيسَ في الآيةِ إِقْرارٌ لِدِينِهِم.');
