INSERT INTO `` (`id`,`page`,`sura_name`,`aya_no`,`quranic_words`,`explanation`) VALUES (11359,603,'المسد',1,'تَبَّتۡ يَدَآ أَبِي لَهَبٖ','خَسِرتْ يدا أبي لَهَبٍ وَشَقِيَ بِإيذائِهِ رَسُولَ اللهِ مُحَمَّداً ﷺ.');
INSERT INTO `` (`id`,`page`,`sura_name`,`aya_no`,`quranic_words`,`explanation`) VALUES (11360,603,'المسد',1,'وَتَبَّ','وَقَد تَحقَّقَ خُسْرانُ أبي لَهَبٍ.');
INSERT INTO `` (`id`,`page`,`sura_name`,`aya_no`,`quranic_words`,`explanation`) VALUES (11361,603,'المسد',2,'مَآ أَغۡنَىٰ عَنۡهُ مَالُهُۥ','ما دَفعَ عنه مالُه، ولَن يَرُدَّ عنْهُ شَيئاً مِنْ عَذابِ اللهِ إذا نَزَلَ بِهِ.');
