INSERT INTO `` (`id`,`page`,`sura_name`,`aya_no`,`quranic_words`,`explanation`) VALUES (11365,603,'المسد',4,'وَٱمۡرَأَتُهُۥ حَمَّالَةَ ٱلۡحَطَبِ','هُوَ وامْرأتُه الَّتِي كانتْ تَحْمِلُ الشَّوكَ، فَتَطْرَحُهُ في طَرِيقِ النَّبِيِّ ﷺ؛ لِأذِيَّتِهِ.');
INSERT INTO `` (`id`,`page`,`sura_name`,`aya_no`,`quranic_words`,`explanation`) VALUES (11366,603,'المسد',5,'فِي جِيدِهَا','في عُنُقِها.');
INSERT INTO `` (`id`,`page`,`sura_name`,`aya_no`,`quranic_words`,`explanation`) VALUES (11367,603,'المسد',5,'مِّن مَّسَدِۢ','حَبْلٌ مُحْكَمُ الفَتْلِ مِن لِيفٍ شَدِيدٍ خَشِنٍ، تُرْفَعُ بِهِ في نارِ جَهَنَّمَ، ثُمَّ تُرْمَى إلى أسْفَلِها.');
INSERT INTO `` (`id`,`page`,`sura_name`,`aya_no`,`quranic_words`,`explanation`) VALUES (11368,604,'الإخلاص',1,'هُوَ ٱللَّهُ أَحَدٌ','هُو اللهُ المتفرِّدُ بالأُلُوهِيَّةِ والرُّبُوبِيَّةِ والأسْماءِ والصِّفاتِ، لا يُشارِكُه أَحَدٌ فِيها.');
INSERT INTO `` (`id`,`page`,`sura_name`,`aya_no`,`quranic_words`,`explanation`) VALUES (11369,604,'الإخلاص',2,'ٱللَّهُ ٱلصَّمَدُ','اللهُ وحْدَهُ السَّيِّدُ الكاملُ الصفات، المَقْصُودُ في قَضاءِ الحوائِجِ.');
INSERT INTO `` (`id`,`page`,`sura_name`,`aya_no`,`quranic_words`,`explanation`) VALUES (11370,604,'الإخلاص',3,'لَمۡ يَلِدۡ وَلَمۡ يُولَدۡ','لَيسَ لَهُ وَلَدٌ ولا والِدٌ ولا صاحِبَةٌ.');
INSERT INTO `` (`id`,`page`,`sura_name`,`aya_no`,`quranic_words`,`explanation`) VALUES (11371,604,'الإخلاص',4,'وَلَمۡ يَكُن لَّهُۥ كُفُوًا أَحَدُۢ','وَلَمْ يَكُن لَهُ مُمَاثِلاً وَلَا مشابهاً أحدٌ مِنْ خَلْقِهِ، لا في أسمائِهِ ولا في صِفاتِهِ، ولا في أفعالِهِ، تَبارك وَتَعالى وَتَقَدَّسَ.');
INSERT INTO `` (`id`,`page`,`sura_name`,`aya_no`,`quranic_words`,`explanation`) VALUES (11372,604,'الفلق',1,'أَعُوذُ بِرَبِّ ٱلۡفَلَقِ','أَعْتَصِمُ بِرَبِّ الصُّبْحِ.');
