INSERT INTO `` (`id`,`page`,`sura_name`,`aya_no`,`quranic_words`,`explanation`) VALUES (11376,604,'الفلق',4,'ٱلنَّفَّٰثَٰتِ فِي ٱلۡعُقَدِ','السَّاحِراتِ اللاتِي يَنفُخْنَ فِيما يَعْقِدْنَ مِنْ عُقَدٍ بِقَصْدِ السِّحْرِ.');
INSERT INTO `` (`id`,`page`,`sura_name`,`aya_no`,`quranic_words`,`explanation`) VALUES (11377,604,'الفلق',5,'وَمِن شَرِّ حَاسِدٍ إِذَا حَسَدَ','وَمِن شَرِّ حاسِدٍ مُبْغِضٍ للنَّاسِ، إذا حَسَدَهُم عَلَى ما وَهَبَهُم اللهُ مِنَ النِّعَمِ.');
