INSERT INTO `` (`id`,`page`,`sura_name`,`aya_no`,`quranic_words`,`explanation`) VALUES (11380,604,'الناس',3,'إِلَٰهِ ٱلنَّاسِ','الَّذِي لا مَعْبُودَ بِحَقٍّ سِوَاهُ.');
INSERT INTO `` (`id`,`page`,`sura_name`,`aya_no`,`quranic_words`,`explanation`) VALUES (11381,604,'الناس',4,'مِن شَرِّ ٱلۡوَسۡوَاسِ','مِن أَذَى الشَّيطانِ الَّذِي يُوسْوِسُ عِنْدَ الغَفْلةِ.');
INSERT INTO `` (`id`,`page`,`sura_name`,`aya_no`,`quranic_words`,`explanation`) VALUES (11382,604,'الناس',4,'ٱلۡخَنَّاسِ','الَّذِي يَخْتفِي عِنْدَ ذِكْرِ اللهِ.');
INSERT INTO `` (`id`,`page`,`sura_name`,`aya_no`,`quranic_words`,`explanation`) VALUES (11383,604,'الناس',5,'يُوَسۡوِسُ فِي صُدُورِ ٱلنَّاسِ','يَبُثُّ الشَّرَّ والشُّكُوكَ في صُدُورِ النَّاسِ.');
INSERT INTO `` (`id`,`page`,`sura_name`,`aya_no`,`quranic_words`,`explanation`) VALUES (11384,604,'الناس',6,'مِنَ ٱلۡجِنَّةِ وَٱلنَّاسِ','مِن شياطِينِ الجِنِّ والإِنْسِ.');
